package com.mapath.bpf.utils;

import org.apache.commons.lang3.StringUtils;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.regex.Matcher;
import java.util.regex.Pattern;

/**
 * Created by zhouxiaobo on 2017/3/9.
 */
public class PicCutUtil {

    private static Logger logger = LoggerFactory.getLogger(PicCutUtil.class);

    public static String spiltImage(String editString) {
        if (StringUtils.isEmpty(editString)) {
            return "";
        }
        int startLoc = editString.indexOf("data:image/jpeg;base64");
        logger.info("start with " + startLoc);

        if (startLoc != -1) {
            int endLoc = 0;
            for (int i = startLoc; i < editString.length(); i++) {
                if ('>' == editString.charAt(i)) {
                    endLoc = i;
                    logger.info("endLoc:" + i);
                    break;
                }
            }

            if (endLoc > startLoc) {
                return editString.substring(startLoc, endLoc + 1);
            }
        }

        return "";
    }

    public static String splitComment(String editString) {
        if (StringUtils.isEmpty(editString)) {
            return "";
        }
            int startLoc = editString.indexOf("data:image/jpeg;base64");
            logger.info("start with " + startLoc);

            if (startLoc != -1) {
                int endLoc = 0;
                for (int i = startLoc; i < editString.length(); i++) {
                    if ('>' == editString.charAt(i)) {
                        endLoc = i;
                        logger.info("endLoc:" + i);
                        break;
                    }
                }

                if (endLoc > startLoc) {
                /*return editString.substring(startLoc,endLoc+1);*/
                    return editString.substring(endLoc + 2);
                }
            }
        return "";
    }

    public static String delHTMLTag(String htmlStr) {
        if (StringUtils.isEmpty(htmlStr)){
            return "";
        }
        String regEx_script = "<script[^>]*?>[\\s\\S]*?<\\/script>"; //定义script的正则表达式
        String regEx_style = "<style[^>]*?>[\\s\\S]*?<\\/style>"; //定义style的正则表达式
        String regEx_html = "<[^>]+>"; //定义HTML标签的正则表达式

        Pattern p_script = Pattern.compile(regEx_script, Pattern.CASE_INSENSITIVE);
        Matcher m_script = p_script.matcher(htmlStr);
        htmlStr = m_script.replaceAll(""); //过滤script标签

        Pattern p_style = Pattern.compile(regEx_style, Pattern.CASE_INSENSITIVE);
        Matcher m_style = p_style.matcher(htmlStr);
        htmlStr = m_style.replaceAll(""); //过滤style标签

        Pattern p_html = Pattern.compile(regEx_html, Pattern.CASE_INSENSITIVE);
        Matcher m_html = p_html.matcher(htmlStr);
        htmlStr = m_html.replaceAll(""); //过滤html标签

        return htmlStr.trim(); //返回文本字符串
    }

    public static void main(String[] args) {
        String str = "<img src=\"data:image/jpeg;base64,\">\n" +
                "                            <blockquote><div align=\"justify\"><div><div><div><div><div><div><div><div><div><div><div><div><div><div><div><div><div><div><div><div><div><div><br></div></div></div></div></div></div></div></div></div></div></div></div></div></div></div></div></div></div></div></div></div></div></div></blockquote>\n" +
                "                            \n" +
                "                        \n" +
                "                        ";

        String str1 = "12121";

        System.out.print(PicCutUtil.spiltImage(str1));
    }
}
